const g=(t,o)=>{t=new Date(t);const n={mm:t.getMonth()+1,dd:t.getDate(),yy:t.getFullYear().toString().slice(-2),yyyy:t.getFullYear(),day:y(t.getDay())};return o.replace(/mm|dd|yy|yyy|day/gi,e=>n[e])},y=t=>["일","월","화","수","목","금","토"][t];function u(t){const n=new Date().getFullYear().toString().slice(-2),e=new Date(t),a=["일","월","화","수","목","금","토"],r=e.getFullYear().toString().slice(-2),s=e.getMonth()+1,c=e.getDate(),i=a[e.getDay()];return`${n!==r?r+".":""} ${s}. ${c} ${i}`}function D(t){const o=new Date(t),n=o.getHours(),e=o.getMinutes();return`${n<12?"오전":"오후"} ${n<=12?n:n-12}:${e<10?"0"+e:e}`}function l(t,o=null){const n=new Date(t),e=o?new Date(o):new Date,a=Math.floor((n.getTime()-e.getTime())/1e3/60);if(a<1)return"방금전";if(a<60)return`${a}분전`;const r=Math.floor(a/60);if(r<24)return`${r}시간전`;const s=Math.floor(a/60/24);if(s<365)return`${s}일전`}export{l as c,u as d,g,D as t};
